import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Client } from '../model/client.model';
import { ClientService } from '../services/client.service';

@Component({
  selector: 'app-add-client',
  templateUrl: './add-client.component.html'
})
export class AddClientComponent implements OnInit {

  newClient = new Client();

  constructor(private clientService: ClientService,
              private router : Router) { }

  ngOnInit(): void {
    }

  addClient(){
    this.clientService.ajouterClient(this.newClient)
    .subscribe(cli => {
      console.log(cli);
      this.router.navigate(['clients']);
    });
    }

}
<div class="container">
    <div>
        <h2>Ajouter un client :</h2>
    </div>
    <form>
        <div class="col-sm-2 col-md-2 col-lg-2">
            <label>ID Commande</label>
            <input type="number" [(ngModel)]="newClient.id" name="id" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Nom</label>
            <input type="text" [(ngModel)]="newClient.nom" name="nom" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Prénom</label>
            <input type="text" [(ngModel)]="newClient.prenom" name="prenom" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Email</label>
            <input type="email" [(ngModel)]="newClient.email" name="email" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Téléphone</label>
            <input type="tel" [(ngModel)]="newClient.telephone" name="telephone" class="form-control">
        </div>
        <div class="mt-2">
            <button type="submit" (click)="addClient()" class="btn btn-success">Ajouter</button>
        </div>
    </form>
</div>
