import { Injectable } from '@angular/core';
import { Commande } from '../model/commande.model';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';


@Injectable({
  providedIn: 'root'
})
export class CommandeService {
    apiURL: string = 'http://localhost:8080/commandes/api';
    commandes : Commande[]; 
  
  constructor(private http : HttpClient) { 
    this.commandes = [{dateCommande :  new Date("08/10/2011"), montantTotal : 256.600},
                    {dateCommande :  new Date("12/08/2011"), montantTotal : 3520.60},
                    {dateCommande :  new Date("09/11/2011"), montantTotal : 5200.0}
                    ];
    
  }

  listeCommande(): Observable<Commande[]> {
    return this.http.get<Commande[]>(this.apiURL);
  }

  ajouterCommande(prod: Commande): Observable<Commande> {
    return this.http.post<Commande>(this.apiURL, prod, httpOptions);
  }

  supprimerCommande(id: number) {
    const url = `${this.apiURL}/${id}`;
    return this.http.delete(url, httpOptions);
  }


  consulterCommande(id: number): Observable<Commande> {
    const url = `${this.apiURL}/${id}`;
    return this.http.get<Commande>(url);
  }

  updateCommande(prod: Commande): Observable<Commande> {
    return this.http.put<Commande>(this.apiURL, prod, httpOptions);
  }

}
