import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Client } from '../model/client.model';
import { ClientService } from '../services/client.service';

@Component({
  selector: 'app-update-client',
  templateUrl: './update-client.component.html',
  styles: [
  ]
})
export class UpdateClientComponent implements OnInit {

  currentClient = new Client();

  constructor(private activatedRoute: ActivatedRoute,
    private router: Router,
    private clientService: ClientService) { }

  ngOnInit(): void {
    this.clientService.consulterClient(this.activatedRoute.snapshot.params['id']).
      subscribe(cli => { this.currentClient = cli; });
  }

  updateClient() {
    this.clientService.updateClient(this.currentClient).subscribe(cli => {
      this.router.navigate(['clients']);
    }
    );
  }

}
<div class="container">
    <div>
        <h2>Modifier un client :</h2>
    </div>
    <form>
        <div class="col-sm-2 col-md-2 col-lg-2">
            <label>ID Client</label>
            <input readonly type="text" [(ngModel)]="currentClient.id" name="idClient" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Nom</label>
            <input type="text" [(ngModel)]="currentClient.nom" name="nom" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Prénom</label>
            <input type="text" [(ngModel)]="currentClient.prenom" name="prenom" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Adresse</label>
            <input type="text" [(ngModel)]="currentClient.adresse" name="adresse" class="form-control">
        </div>
        <div class="mt-2">
            <button type="submit" (click)="updateClient()" class="btn btn-success">Modifier</button>
        </div>
    </form>
</div>
